 var optionsAvbl= document.querySelectorAll("[data-modal]");

    optionsAvbl.forEach(option => {
        option.addEventListener("click", (e) => {
            changeSizeChartModal(e.target.getAttribute("data-modal").substring(1)),
            option.classList.add("list-active")
        });
    });

var select = document.querySelector("#guide-opt");

select.addEventListener("change", () => {
    var selectedString = select.options[select.selectedIndex].value;
changeSizeChartModal(selectedString.substring(1))
})
    var modals = document.querySelectorAll(".guide-fabric-modal");

    function changeSizeChartModal(selectedId) {
        optionsAvbl.forEach(option => {
            option.classList.remove("list-active");
        });
        modals.forEach(modal => {
            var modalId = modal.id;
            if(modalId == selectedId)
                modal.classList.add("modal-active");
            else
                modal.classList.remove("modal-active");
        })
    }

css_global:
.guide-wrapper {
    font-family: 'Poppins', Arial, sans-serif;
    font-size: 16px;
    font-weight: 400;
    letter-spacing: .2px;
    color: #3C3C3C;
    max-width: 1300px;
    margin: auto;
}

.row-spacing {
    padding-top: 50px;
}

.row.row-spacing.guide-row {
    max-width: 100%;
    margin: 0;
}

.guide-row {
    text-align: center;
}

.guide-row h1,
h2 {
    font-family: 'Poppins', Arial, sans-serif;
    color: #3A4369;
    font-weight: 600;
}

.guide-row h1 {
    font-size: 28px;
    padding-bottom: 50px;
    margin-bottom: 0;
}

.guide-row h2 {
    font-size: 20px;
}

.guide-row p {
    text-align: left;
    padding-top: 50px;
    max-width: 680px;
    margin: auto;
}

.guide-row ul {
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
    margin: auto 20%;
    padding: 0 0 20px 0;
}

.guide-row ul li {
    list-style: none;
}

.guide-blue-600,
.welt-title {
    color: #3A4369;
    font-weight: 600;
}

.intro-copy{width:64%;margin:auto;}

.guide-divider {
    border-bottom: 1px solid #707070;
    max-width: 320px;
    width: 40%;
    padding-top: 50px;
    margin: auto;
}

.guide-fabric-list {
    padding-top: 50px;
}

.guide-fabric-list ul {
    margin: 0;
    padding: 0;
    display: flex;
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    flex-flow: row wrap;
    justify-content: space-between;
}

.guide-fabric-list ul li {
    width: 155px;
    height: 210px;
    background-color: #f4f4f4;
    list-style: none;
    margin-bottom: 12px;
    padding: 35px 12px;
    cursor: pointer;
    display: flex;
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    flex-flow: column wrap;
    justify-content: start;
    opacity: 1;
    transition: opacity .25s ease-in-out;
    -moz-transition: opacity .25s ease-in-out;
    -webkit-transition: opacity .25s ease-in-out;
}

.guide-fabric-list ul li:hover {
    opacity: .5;
}

.guide-fabric-list ul li p {
    text-align: center;
    padding-top: 15px;
    margin: 0;
    color: #7c7c7c;
    font-size: 13px;
    font-weight: 100;
    letter-spacing: .2px;
}

.guide-btn {
    cursor: pointer;
}

#guide-opt {
    display: none;
}

.fabric-btn {
    cursor: pointer;
    opacity: 1;
    transition: opacity .25s ease-in-out;
    -moz-transition: opacity .25s ease-in-out;
    -webkit-transition: opacity .25s ease-in-out;
}

.fabric-btn:hover {
    opacity: 0.5;
}

.gfl-title {
    font-size: 16px;
    font-weight: 600;
    color: #3A4369;
}

.guide-fabric-modal {
    padding: 35px;
    background-color: #ffffff;
    margin: auto;
    display: none;
}

.table-overflow {
    overflow-x: scroll;
    -ms-overflow-style: none;
    overflow-style: none;
}

.table-overflow::-webkit-scrollbar{
    display: none;
}

.modal-active {
    display: block;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-around;
}

.scroll-more {
    text-align: right;
    display: none;
}

.fabric-title {
    font-family: 'Poppins', Arial, sans-serif;
    color: #3A4369;
    font-weight: 600;
    font-size: 20px;
    border-bottom: 1px solid #707070;
    padding-right: 50px;
    padding-bottom: 20px;
    margin-bottom: 20px;
    text-align: left;
}

.fabric-title span {
    color: #7C7C7C;
    font-size: 14px;
    font-weight: 200;
    padding-left: 50px;
}

.fabric-title-ul {
    font-family: 'Poppins', Arial, sans-serif;
    color: #3A4369;
    font-weight: 600;
    font-size: 20px;
    margin-left: -1em;
}


.line-cta {
    margin-top: 20px;
}

.line-cta a {
    font-size: 14px;
    text-decoration: underline;
    color: #3A4369;
    font-weight: 600;
}

.line-cta a:hover {
    text-decoration: none;
}

.list-arrow {
    display-none;
}

.list-active {
    border-bottom: #3A4369 solid 2px;
}

.box-493 {
    width: 493px;
