    background-color: #f4f4f4;
    text-align: left;
}

.size-divs ul {
    text-align: left;
    height: 100%;
    width: 100%;
    margin-left: 0;
    margin-top: 0;
    background-color: #f4f4f4;
    display: flex;
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    flex-flow: column wrap;
    justify-content: space-around;
}

.welt-img {
    max-width: 500px;
    margin-right: 25px;
}
/**Table CSS**/

table.welt-table tr td.table-head {
    background-color: #838383;
    color: #ffffff;
    text-align: left;
    font-size: 1.2em;
    letter-spacing: 1px;
}

table.welt-table {
    border: #e6e6e6 solid 1px;
    margin: auto;
    text-align: center;
    max-width: 640px;
}

.welt-table tr,
.welt-table td,
.welt-table th {
    border: #e6e6e6 solid 1px;
    vertical-align: middle;
    text-align: center;
}

table.welt-table tbody tr th {
    color: #3A4369;
    font-weight: 600;
    padding: 0.95385rem;
}

table.welt-table tbody tr td {
    vertical-align: middle;
}

table.welt-table tr:nth-of-type(even) {
    background-color: #ededed;
}

table.welt-table tr:nth-of-type(odd) {
    background-color: #fafafa;
}

.welt-title {
    text-align: center;
    display: none;
}

.welt-copy-box {
    display: inline-block;
    font-size: 13px;
}

.welt-copy-sm {
    font-size: 12px;
    font-style: italic;
    line-height: 20px;
    max-width: 580px;
    margin-left: auto;
    margin-right: auto;
    text-align: left;
    padding-top: 50px;
}

li.fabric-btn.fabric-arrow::after {
    content: "\203A";
    padding-left: 10px;
}

.link-active {
    border-bottom: red solid 1px;
}

.copy-bold {
    font-weight: 600;
    padding-right: 20px;
}

.size-div-link {
    position: absolute;
    background-color: #f4f4f4;
    display: none;
}

#welt-twin {
    display: block;
}

.guide-cta a {
    width: 320px;
    background-color: #3A4369;
    color: #ffffff;
    font-size: 16px;
    display: inline-block;
    padding-top: 12px;
    padding-bottom: 12px;
    text-decoration: none;
}

.guide-cta a:hover {
    background-color: #656875;
}

.nowrap{white-space:nowrap}
.mob-copy{display:none;}
/**********Media Queries**********/

@media all and (max-width: 960px) {
    .guide-wrapper {
        margin-left: 5%;
        margin-right: 5%;
    }
    .guide-fabric-list ul {
        justify-content: flex-start;
    }
    .guide-fabric-list ul li {
        margin-left: 10px;
        margin-right: 10px;
    }
    .guide-row ul {
        margin: auto 10%;
    }
    .modal-active {
        flex-direction: column-reverse;
    }
    .welt-img {
        margin: 50px auto auto auto;
    }
    .show-960 {
        display: block;
    }
}

@media all and (max-width:870px) {
    .show-870 {display: block;}
    .intro-copy{width:70%;}
}

@media all and (max-width:775px) {
    .show-775 {display: block;}
}

@media all and (max-width: 640px) {
    .intro-copy{width:95%;}

    .modal-inner-wrap .modal-content {
    	padding: 0px
    }

    #guide-opt {
        display: block;
    }
    .guide-btn {
        display: none;
    }
    .guide-fabric-list ul {
        justify-content: flex-start;
        flex-flow: column;
    }
    .guide-fabric-list ul li {
        width: 100%;
        height: auto;
        text-align: left;
        padding: 15px;
        position: relative;
    }
    .guide-fabric-list ul li p {
        text-align: left;
        padding-top: 5px;
    }
    .guide-fabric-modal {
        width: 100%;
        height: auto;
        margin: auto;
    }
    .fabric-box-group {
        flex-flow: column nowrap;
    }
    .fabric-title {
        text-align: center;
        padding-right: 0;
        border-bottom: none;
    }
    .fabric-box img {
        width: 100%;
        padding-right: 0;
    }
    ._lgc-css .slick-slider {
        padding-left: 0;
        padding-right: 0;
    }
    .guide-cta a {
        width: 90%;
    }
    .size-links,
    .fabric-title span {
        display: none;
    }
    .welt-title {
        display: block;
        padding-bottom: 20px;
        padding-top: 30px;
    }
    .size-div-link {
        display: block;
    }
    .size-divs {
        width: 90%;
        text-align: center;
    }
    .welt-slider {
        display: block;
        background-color: #f4f4f4;
        margin-top: 10px;
    }
    .box-493 {
        height: 400px;
        display: none;
    }
    .welt-copy-sm {
        max-width: none;
        text-align: left;
    }
    .welt-copy-box {
        width: 48%;
        text-align: left;
    }
    table.welt-table tbody tr td {
        padding: 0.95385rem 0.45385rem;
    }
    .copy-bold {
        text-align: right;
    }
    .box-493 ul li {
        align-self: center;
    }
    .list-arrow {
        height: 100%;
        width: 100%;
        background-image: url({{media url="pub/media/wysiwyg/size_chart/welt-size-chart-woman"}});
        background-repeat: no-repeat;
        background-size: 35px 35px;
        background-position: right;
        margin: -15px;
        position: absolute;
    }
    ._lgc-css .slick-dots {
        width: 100%;
        bottom: auto;
    }
    .show-640 {
        display: block;
    }
}

@media all and (max-width:450px) {
    .show- {display: block;}
}

@media all and (max-width:414px){
    .guide-fabric-modal{padding: 35px 0;}
    .mob-pad{padding-top: 25px;}
    .guide-row h1 {padding-bottom: 25px;}
}

@media all and (max-width:375px){
    .guide-fabric-modal{padding: 35px 0px;}
}

@media all and (max-width:320px){
    .guide-fabric-modal{padding: 35px 0 0 0;}
    .intro-copy{width:100%;display:none;}
    .mob-copy{display:block;}
    table.welt-table tbody tr th {padding: 0.95385rem .1rem;}
}
