custom_js:
const accordionItemHeaders = document.querySelectorAll(".accordion-item-header");

accordionItemHeaders.forEach(accordionItemHeader => {
  accordionItemHeader.addEventListener("click", event => {
accordionItemHeader.classList.toggle("active-acc");
    const accordionItemBody = accordionItemHeader.nextElementSibling;
    if(accordionItemHeader.classList.contains("active-acc")) {
      accordionItemBody.classList.toggle("acc-open");
      accordionItemBody.style.maxHeight = accordionItemBody.scrollHeight + "px";
    }
    else {
      accordionItemBody.style.maxHeight = 0;
      accordionItemBody.classList.remove("acc-open");

    }

  });
});

css_global:
.social-wrapper {
    display: flex;
    align-items: center;
}

.w.footer-v5 {
    padding-bottom: 40px;
    padding-top: 10px;
    z-index: 0;
    position: relative;
}

.footer-v5 .social-text {
    color: #cccccc;
}

.social-text:hover {
    text-decoration: underline;
}

.footer-v5-list {
    line-height: 2;
}

.footer-v5 .footer-v5-title {
    margin-top: 1rem;
    margin-bottom: 1rem;
    font-size: 17px;
    position: relative;
}

.white-lnk {
    color: #ccc;
}

.details-v5 {
    font-size: 10px;
    margin-top: 5px;
}

.footer.content .horizontal-list {
    display: flex;
    padding: 0px;
}

.first-store {
    padding-right: 10px;
    border-right: 1px solid #ccc;
}

.stores {
    padding-right: 10px;
    padding-left: 10px;
    border-right: 1px solid #ccc;
}

.last-store {
    padding-left: 10px;
}

.footer-v5 .title-stores {
    margin: 0;
    padding-bottom: 5px;
}

.social-icons {
    margin: 0 15px 0 0;
    height: 35px;
    width: 35px;
    font-size: 17px;
}

.col-md-2 {
    width: 19.666667%
}

.footer-v5 .underline {
    text-decoration: underline;
    color: #cccccc;
}

.footer-v5 li:hover {
    text-decoration: underline;
}

.separation-border {
    padding: 0;
    width: 1px;
    background-color: #ccc;
    margin-right: 6%;
}

.footer-section2-content {
    display: flex;
}

.theme-pearl.store-view-default .newsletter-btn {
    border: 1px solid white;
    margin-top: 0;
}

.newsletter-subscribe .form-group {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}


@media only screen and (min-width: 786px) {
    .theme-pearl .w.footer-v5 .mini-logo {
        padding-top: 20px;
    }

    .w.footer-v5 #newsletter-bottom-error {
        order: 3;
        font-size: 12px;
        color: red;
        position: absolute;
        top: 100%;
        left: 5px;
    }

    .w.footer-v5 .validate-email.newsletter-error {
        border: 1px solid red;
    }

    .w.footer-v5 .form-group {
        position: relative;
        margin-bottom: 20px;
    }
}

@media only screen and (max-width: 786px) {
    .page-wrapper .page-footer .footer.content .footer-v1-content .accordion-item-header {
        min-height: 3.5rem;
        line-height: 1.25rem;
        padding: 0.5rem 3rem 0.5rem 1rem;
        font-weight: bold;
        position: relative;
        font-size: 16px;
        text-transform: uppercase;
        font-weight: 600;
        cursor: pointer;
        display: flex;
        align-items: center;
        justify-content: center;
        width: 90%;
        margin-bottom: 0;
    }

    .page-wrapper .page-footer .footer .accordion-item-header::after {
        content: " \002B ";
        font-size: 2rem;
        position: absolute;
        right: 0;
        border: none;
        margin: 0;
        height: auto;
        width: auto;
        top: 50%;
        transform: translateY(-60%);
    }

    .page-wrapper .page-footer .footer .accordion-item-header.active-acc::after {
        content: " \2212 ";
    }

    .accordion-item-body {
        max-height: 0;
        overflow: hidden;
        transition: max-height 0.2s ease-out;
        width: 100%;
    }

    .page-wrapper .page-footer .footer .accordion-item-header.active-acc
    .accordion-item-body-content {
        padding: 1rem;
        line-height: 1.5rem;
    }

    .footer-v5 .accordion {
        width: 100%;
        border-top: 1px solid #ccc;
        margin: 0;
    }

    .m-auto {
        margin-left: auto;
        margin-right: auto;
    }

    .horizontal-list {
        justify-content: center;
    }

    .footer-section2-content {
        display: block;
    }

    .separation-border, .social-text {
        display: none;
    }

    .footer-v5 .acc-open .accordion-item-body-content {
        margin-bottom: 20px;
    }

    .social-icons-section.acc-open {
        margin-bottom: 20px;
    }

    .mg-mobile {
        display: flex;
        justify-content: center;
    }

    .col-md-2 {
        width: 100%;
    }

    .w.footer-v5 #newsletter-bottom-error {
        order: 3;
        font-size: 12px;
        color: red;
        position: absolute;
        left: 35px;
        top: 100%;
    }
    .w.footer-v5 .validate-email.newsletter-error {
        border: 1px solid red;
    }

    .w.footer-v5 .form-group {
        margin-bottom: 20px;
        position: relative;
    }
}
